import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaHeart, FaShoppingCart, FaTrash } from 'react-icons/fa';
import { toast } from 'react-toastify';
import './Wishlist.css';

const Wishlist = () => {
  const [wishlistItems, setWishlistItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchWishlist();
  }, []);

  const fetchWishlist = async () => {
    try {
      const userInfo = JSON.parse(localStorage.getItem('userInfo') || '{}');
      if (!userInfo.token) {
        setLoading(false);
        return;
      }

      const { data } = await axios.get('/api/users/wishlist', {
        headers: { Authorization: `Bearer ${userInfo.token}` }
      });
      
      setWishlistItems(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching wishlist:', error);
      setLoading(false);
    }
  };

  const removeFromWishlist = async (productId) => {
    try {
      const userInfo = JSON.parse(localStorage.getItem('userInfo') || '{}');
      if (!userInfo.token) {
        toast.error('Please login to manage wishlist');
        return;
      }

      await axios.delete(`/api/users/wishlist/${productId}`, {
        headers: { Authorization: `Bearer ${userInfo.token}` }
      });

      setWishlistItems(prev => prev.filter(item => item._id !== productId));
      toast.success('Removed from wishlist');
    } catch (error) {
      toast.error('Error removing from wishlist');
    }
  };

  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem('cartItems') || '[]');
    const existingItem = existingCart.find(item => item._id === product._id);
    
    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      existingCart.push({ 
        ...product, 
        quantity: 1, 
        selectedSize: 'M', 
        selectedColor: product.colors[0] 
      });
    }
    
    localStorage.setItem('cartItems', JSON.stringify(existingCart));
    toast.success('Added to cart!');
  };

  if (loading) {
    return (
      <div className="wishlist-page">
        <div className="container">
          <div className="loading">Loading wishlist...</div>
        </div>
      </div>
    );
  }

  if (wishlistItems.length === 0) {
    return (
      <div className="wishlist-page">
        <div className="container">
          <div className="empty-wishlist">
            <FaHeart className="empty-wishlist-icon" />
            <h2>Your wishlist is empty</h2>
            <p>Save your favorite items for later by clicking the heart icon</p>
            <Link to="/products" className="btn btn-primary">
              Explore Products
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="wishlist-page">
      <div className="container">
        <div className="wishlist-header">
          <h1>My Wishlist</h1>
          <p>{wishlistItems.length} items saved</p>
        </div>

        <div className="wishlist-grid">
          {wishlistItems.map(product => (
            <div key={product._id} className="wishlist-item">
              <div className="item-image">
                <img 
                  src={product.images[0] || '/api/placeholder/300/300'} 
                  alt={product.name} 
                />
                <button 
                  className="remove-btn"
                  onClick={() => removeFromWishlist(product._id)}
                  title="Remove from wishlist"
                >
                  <FaTrash />
                </button>
              </div>

              <div className="item-details">
                <div className="item-brand">{product.brand}</div>
                <h3 className="item-name">{product.name}</h3>
                <p className="item-description">{product.description.substring(0, 100)}...</p>
                
                <div className="item-colors">
                  {product.colors.slice(0, 4).map((color, index) => (
                    <span 
                      key={index} 
                      className="color-dot" 
                      style={{ backgroundColor: color.toLowerCase() }}
                      title={color}
                    ></span>
                  ))}
                </div>

                <div className="item-sizes">
                  <span className="sizes-label">Sizes:</span>
                  {product.sizes.map((size, index) => (
                    <span key={index} className="size-badge">{size}</span>
                  ))}
                </div>

                <div className="item-price">
                  <span className="current-price">${product.price}</span>
                  {product.originalPrice > product.price && (
                    <span className="original-price">${product.originalPrice}</span>
                  )}
                </div>

                <div className="item-actions">
                  <button 
                    className="btn btn-primary add-to-cart-btn"
                    onClick={() => addToCart(product)}
                  >
                    <FaShoppingCart />
                    Add to Cart
                  </button>
                  <button 
                    className="btn btn-outline view-btn"
                    onClick={() => window.open(`/products/${product._id}`, '_blank')}
                  >
                    View Details
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Wishlist;
