import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaEye, FaEdit, FaSearch, FaFilter } from 'react-icons/fa';
import { toast } from 'react-toastify';
import './AllOrders.css';

const AllOrders = () => {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [sortBy, setSortBy] = useState('newest');

  useEffect(() => {
    fetchOrders();
  }, []);

  useEffect(() => {
    filterAndSortOrders();
  }, [orders, searchTerm, statusFilter, sortBy]);

  const fetchOrders = async () => {
    try {
      const adminInfo = JSON.parse(localStorage.getItem('adminInfo') || '{}');
      if (!adminInfo.token) {
        window.location.href = '/admin/login';
        return;
      }

      // Simulate API call with mock data
      const mockOrders = [
        {
          _id: '64abc123def456',
          user: { name: 'Sarah Johnson', email: 'sarah@email.com' },
          orderItems: [
            { name: 'High-Performance Sports Bra', quantity: 2, price: 49.99 },
            { name: 'Yoga Leggings', quantity: 1, price: 69.99 }
          ],
          totalPrice: 169.97,
          status: 'processing',
          createdAt: '2024-01-15T10:30:00Z',
          shippingAddress: {
            fullName: 'Sarah Johnson',
            address: '123 Fitness St',
            city: 'New York',
            postalCode: '10001'
          }
        },
        {
          _id: '64abc456def789',
          user: { name: 'Emma Wilson', email: 'emma@email.com' },
          orderItems: [
            { name: 'Running Tank Top', quantity: 3, price: 29.99 }
          ],
          totalPrice: 89.97,
          status: 'shipped',
          createdAt: '2024-01-14T15:45:00Z',
          shippingAddress: {
            fullName: 'Emma Wilson',
            address: '456 Health Ave',
            city: 'Los Angeles',
            postalCode: '90210'
          }
        },
        {
          _id: '64abc789def012',
          user: { name: 'Lisa Brown', email: 'lisa@email.com' },
          orderItems: [
            { name: 'CrossFit Shorts', quantity: 2, price: 39.99 },
            { name: 'Sports Bra Set', quantity: 1, price: 79.99 }
          ],
          totalPrice: 159.97,
          status: 'delivered',
          createdAt: '2024-01-13T09:20:00Z',
          shippingAddress: {
            fullName: 'Lisa Brown',
            address: '789 Wellness Blvd',
            city: 'Chicago',
            postalCode: '60601'
          }
        },
        {
          _id: '64abc012def345',
          user: { name: 'Maria Garcia', email: 'maria@email.com' },
          orderItems: [
            { name: 'Compression Leggings', quantity: 1, price: 89.99 }
          ],
          totalPrice: 89.99,
          status: 'pending',
          createdAt: '2024-01-16T14:20:00Z',
          shippingAddress: {
            fullName: 'Maria Garcia',
            address: '321 Active St',
            city: 'Miami',
            postalCode: '33101'
          }
        }
      ];

      setOrders(mockOrders);
      setLoading(false);
    } catch (error) {
      toast.error('Error loading orders');
      setLoading(false);
    }
  };

  const filterAndSortOrders = () => {
    let filtered = orders.filter(order => {
      const matchesSearch = 
        order._id.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.user.email.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesStatus = statusFilter === 'all' || order.status === statusFilter;
      
      return matchesSearch && matchesStatus;
    });

    // Sort orders
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'newest':
          return new Date(b.createdAt) - new Date(a.createdAt);
        case 'oldest':
          return new Date(a.createdAt) - new Date(b.createdAt);
        case 'highest':
          return b.totalPrice - a.totalPrice;
        case 'lowest':
          return a.totalPrice - b.totalPrice;
        default:
          return 0;
      }
    });

    setFilteredOrders(filtered);
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      const adminInfo = JSON.parse(localStorage.getItem('adminInfo') || '{}');
      
      // Update local state immediately for better UX
      setOrders(prev => prev.map(order =>
        order._id === orderId ? { ...order, status: newStatus } : order
      ));

      // In a real app, make API call here
      toast.success('Order status updated successfully');
    } catch (error) {
      toast.error('Error updating order status');
    }
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'pending':
        return 'status-pending';
      case 'processing':
        return 'status-processing';
      case 'shipped':
        return 'status-shipped';
      case 'delivered':
        return 'status-delivered';
      case 'cancelled':
        return 'status-cancelled';
      default:
        return 'status-pending';
    }
  };

  if (loading) {
    return (
      <div className="all-orders-page">
        <div className="container">
          <div className="loading">Loading orders...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="all-orders-page">
      <div className="container">
        <div className="page-header">
          <h1>All Orders</h1>
          <p>Manage and track all customer orders</p>
        </div>

        {/* Filters and Search */}
        <div className="orders-controls">
          <div className="search-bar">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search orders by ID, customer name, or email..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filters">
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Status</option>
              <option value="pending">Pending</option>
              <option value="processing">Processing</option>
              <option value="shipped">Shipped</option>
              <option value="delivered">Delivered</option>
              <option value="cancelled">Cancelled</option>
            </select>

            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="filter-select"
            >
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
              <option value="highest">Highest Amount</option>
              <option value="lowest">Lowest Amount</option>
            </select>
          </div>
        </div>

        {/* Orders Table */}
        <div className="orders-table">
          <div className="table-header">
            <span>Order ID</span>
            <span>Customer</span>
            <span>Items</span>
            <span>Total</span>
            <span>Status</span>
            <span>Date</span>
            <span>Actions</span>
          </div>

          {filteredOrders.length > 0 ? (
            filteredOrders.map(order => (
              <div key={order._id} className="table-row">
                <span className="order-id">#{order._id.slice(-8).toUpperCase()}</span>
                
                <div className="customer-info">
                  <div className="customer-name">{order.user.name}</div>
                  <div className="customer-email">{order.user.email}</div>
                </div>

                <div className="order-items">
                  <div className="items-count">{order.orderItems.length} items</div>
                  <div className="items-preview">
                    {order.orderItems.slice(0, 2).map((item, index) => (
                      <span key={index}>{item.name}</span>
                    ))}
                    {order.orderItems.length > 2 && (
                      <span>+{order.orderItems.length - 2} more</span>
                    )}
                  </div>
                </div>

                <span className="order-total">${order.totalPrice.toFixed(2)}</span>

                <div className="status-column">
                  <select
                    value={order.status}
                    onChange={(e) => updateOrderStatus(order._id, e.target.value)}
                    className={`status-select ${getStatusClass(order.status)}`}
                  >
                    <option value="pending">Pending</option>
                    <option value="processing">Processing</option>
                    <option value="shipped">Shipped</option>
                    <option value="delivered">Delivered</option>
                    <option value="cancelled">Cancelled</option>
                  </select>
                </div>

                <span className="order-date">
                  {new Date(order.createdAt).toLocaleDateString()}
                </span>

                <div className="actions">
                  <button className="action-btn view" title="View Details">
                    <FaEye />
                  </button>
                  <button className="action-btn edit" title="Edit Order">
                    <FaEdit />
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div className="no-orders">
              <h3>No orders found</h3>
              <p>No orders match your search criteria</p>
            </div>
          )}
        </div>

        {/* Summary Stats */}
        <div className="orders-summary">
          <div className="summary-card">
            <h4>Total Orders</h4>
            <span>{filteredOrders.length}</span>
          </div>
          <div className="summary-card">
            <h4>Total Revenue</h4>
            <span>
              ${filteredOrders.reduce((sum, order) => sum + order.totalPrice, 0).toFixed(2)}
            </span>
          </div>
          <div className="summary-card">
            <h4>Pending Orders</h4>
            <span>
              {filteredOrders.filter(order => order.status === 'pending').length}
            </span>
          </div>
          <div className="summary-card">
            <h4>Processing Orders</h4>
            <span>
              {filteredOrders.filter(order => order.status === 'processing').length}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AllOrders;
