// client/src/pages/User/Signup.jsx
import React, { useState } from 'react';
import './Signup.css';

const Signup = () => {
  const [formData, setFormData] = useState({
    name: '', email: '', otp: '', password: '', confirmPassword: ''
  });
  const [errors, setErrors] = useState({});
  const [otpSent, setOtpSent] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const sendOtp = async () => {
  if (!formData.email.endsWith('.com')) {
    setErrors({ email: 'Email must end with .com' });
    return;
  }
  try {
    await fetch('http://localhost:5000/api/otp/send-otp', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email: formData.email }),
    });
    setOtpSent(true);
    alert('OTP sent to your email!');
  } catch (err) {
    alert('Failed to send OTP');
  }
};

  const validate = () => {
    const errs = {};
    if (!formData.name) errs.name = 'Name is required';
    if (!formData.email.endsWith('.com')) errs.email = 'Email must end with .com';
    if (!formData.otp) errs.otp = 'OTP required';
    if (formData.password.length < 6) errs.password = 'Password must be at least 6 characters';
    if (formData.password !== formData.confirmPassword) errs.confirmPassword = 'Passwords do not match';
    return errs;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) return setErrors(validationErrors);

    try {
      const res = await fetch('/api/users/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      const data = await res.json();
      alert(data.message || 'Signup Successful!');
    } catch (error) {
      alert('Signup Failed');
    }
  };

  return (
    <div className="signup-container">
      <form onSubmit={handleSubmit} className="signup-form">
        <h2>Signup</h2>

        <input type="text" name="name" placeholder="Full Name" onChange={handleChange} />
        {errors.name && <span className="error">{errors.name}</span>}

        <input type="email" name="email" placeholder="Email (.com only)" onChange={handleChange} />
        {errors.email && <span className="error">{errors.email}</span>}

        <button type="button" onClick={sendOtp} className="otp-btn">Send OTP</button>

        <input type="text" name="otp" placeholder="Enter OTP" onChange={handleChange} />
        {errors.otp && <span className="error">{errors.otp}</span>}

        <input type="password" name="password" placeholder="Password" onChange={handleChange} />
        {errors.password && <span className="error">{errors.password}</span>}

        <input type="password" name="confirmPassword" placeholder="Confirm Password" onChange={handleChange} />
        {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}

        <button type="submit" className="signup-btn">Create Account</button>
      </form>
    </div>
  );
};

export default Signup;
