import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // ✅ use the custom hook
import './Navbar.css';

const Navbar = () => {
  const { user, logout } = useAuth(); // ✅ useAuth instead of useContext
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="logo">BigCommerce</Link>
        <ul className="nav-links">
          <li><Link to="/" className={isActive('/') ? 'active' : ''}>Home</Link></li>
          <li><Link to="/products" className={isActive('/products') ? 'active' : ''}>Products</Link></li>
          <li><Link to="/cart" className={isActive('/cart') ? 'active' : ''}>Cart</Link></li>
          <li><Link to="/wishlist" className={isActive('/wishlist') ? 'active' : ''}>Wishlist</Link></li>
          <li><Link to="/contact" className={isActive('/contact') ? 'active' : ''}>Contact</Link></li>
          <li><Link to="/about" className={isActive('/about') ? 'active' : ''}>About</Link></li>
        </ul>
        <div className="auth-buttons">
          {user ? (
            <>
              <span className="welcome-text">Hi, {user.name}</span>
              <button className="logout-btn" onClick={logout}>Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" className="auth-link">Login</Link>
              <Link to="/signup" className="auth-link signup">Signup</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
