import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaEye, FaBox, FaTruck, FaCheckCircle, FaClock } from 'react-icons/fa';
import { toast } from 'react-toastify';
import './Orders.css';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const userInfo = JSON.parse(localStorage.getItem('userInfo') || '{}');
      if (!userInfo.token) {
        toast.error('Please login to view orders');
        setLoading(false);
        return;
      }

      const { data } = await axios.get('/api/orders/myorders', {
        headers: { Authorization: `Bearer ${userInfo.token}` }
      });

      setOrders(data);
      setLoading(false);
    } catch (error) {
      toast.error('Error loading orders');
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending':
        return <FaClock className="status-icon pending" />;
      case 'processing':
        return <FaBox className="status-icon processing" />;
      case 'shipped':
        return <FaTruck className="status-icon shipped" />;
      case 'delivered':
        return <FaCheckCircle className="status-icon delivered" />;
      default:
        return <FaClock className="status-icon pending" />;
    }
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'pending':
        return 'status-pending';
      case 'processing':
        return 'status-processing';
      case 'shipped':
        return 'status-shipped';
      case 'delivered':
        return 'status-delivered';
      default:
        return 'status-pending';
    }
  };

  if (loading) {
    return (
      <div className="orders-page">
        <div className="container">
          <div className="loading">Loading orders...</div>
        </div>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="orders-page">
        <div className="container">
          <div className="empty-orders">
            <FaBox className="empty-orders-icon" />
            <h2>No orders yet</h2>
            <p>When you place orders, they will appear here</p>
            <Link to="/products" className="btn btn-primary">
              Start Shopping
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="orders-page">
      <div className="container">
        <div className="orders-header">
          <h1>My Orders</h1>
          <p>{orders.length} order{orders.length !== 1 ? 's' : ''} found</p>
        </div>

        <div className="orders-list">
          {orders.map(order => (
            <div key={order._id} className="order-card">
              <div className="order-header">
                <div className="order-info">
                  <h3>Order #{order._id.slice(-8).toUpperCase()}</h3>
                  <p>Placed on {new Date(order.createdAt).toLocaleDateString()}</p>
                </div>
                <div className="order-status">
                  {getStatusIcon(order.status)}
                  <span className={`status-text ${getStatusClass(order.status)}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                </div>
                <div className="order-total">
                  <span className="total-amount">${order.totalPrice.toFixed(2)}</span>
                </div>
              </div>

              <div className="order-items">
                {order.orderItems.slice(0, 3).map((item, index) => (
                  <div key={index} className="order-item">
                    <img 
                      src={item.image || '/api/placeholder/60/60'} 
                      alt={item.name} 
                    />
                    <div className="item-details">
                      <h4>{item.name}</h4>
                      <p>Qty: {item.quantity} | Size: {item.size} | Color: {item.color}</p>
                      <span className="item-price">${item.price}</span>
                    </div>
                  </div>
                ))}
                {order.orderItems.length > 3 && (
                  <div className="more-items">
                    +{order.orderItems.length - 3} more item{order.orderItems.length - 3 !== 1 ? 's' : ''}
                  </div>
                )}
              </div>

              <div className="order-actions">
                <Link 
                  to={`/orders/${order._id}`} 
                  className="btn btn-outline view-order-btn"
                >
                  <FaEye /> View Details
                </Link>
                {order.status === 'delivered' && (
                  <button className="btn btn-primary reorder-btn">
                    Reorder
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orders;
