import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Wishlist.css';

const Wishlist = () => {
  const [wishlist, setWishlist] = useState([]);

  const fetchWishlist = async () => {
    try {
      const response = await axios.get('/api/wishlist'); // Adjust the route as needed
      setWishlist(response.data);
    } catch (error) {
      console.error('Error fetching wishlist:', error);
    }
  };

  const removeFromWishlist = async (id) => {
    try {
      await axios.delete(`/api/wishlist/${id}`);
      setWishlist(wishlist.filter((item) => item._id !== id));
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  useEffect(() => {
    fetchWishlist();
  }, []);

  return (
    <div className="wishlist-container">
      <h1 className="wishlist-title">Your Wishlist</h1>
      {wishlist.length === 0 ? (
        <p className="wishlist-empty">No items in wishlist.</p>
      ) : (
        <div className="wishlist-grid">
          {wishlist.map((item) => (
            <div key={item._id} className="wishlist-card">
              <img src={item.image} alt={item.name} className="wishlist-img" />
              <h3>{item.name}</h3>
              <p>â‚¹{item.price}</p>
              <div className="wishlist-buttons">
                <button className="remove-btn" onClick={() => removeFromWishlist(item._id)}>Remove</button>
                <button className="add-btn">Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
