import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { FaCreditCard, FaLock, FaTruck } from 'react-icons/fa';
import './Checkout.css';

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [shippingAddress, setShippingAddress] = useState({
    fullName: '',
    address: '',
    city: '',
    postalCode: '',
    country: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('paypal');
  const [loading, setLoading] = useState(false);
  const [total, setTotal] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    const items = JSON.parse(localStorage.getItem('cartItems') || '[]');
    if (items.length === 0) {
      navigate('/cart');
      return;
    }
    setCartItems(items);
    calculateTotal(items);

    // Load saved shipping address if exists
    const savedAddress = JSON.parse(localStorage.getItem('shippingAddress') || '{}');
    if (savedAddress.fullName) {
      setShippingAddress(savedAddress);
    }
  }, [navigate]);

  const calculateTotal = (items) => {
    const subtotal = items.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setTotal(subtotal);
  };

  const handleAddressChange = (e) => {
    setShippingAddress({
      ...shippingAddress,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const userInfo = JSON.parse(localStorage.getItem('userInfo') || '{}');
      if (!userInfo.token) {
        toast.error('Please login to place order');
        navigate('/login');
        return;
      }

      // Save shipping address
      localStorage.setItem('shippingAddress', JSON.stringify(shippingAddress));

      const orderData = {
        orderItems: cartItems.map(item => ({
          product: item._id,
          name: item.name,
          quantity: item.quantity,
          price: item.price,
          size: item.selectedSize,
          color: item.selectedColor,
          image: item.images[0]
        })),
        shippingAddress,
        paymentMethod,
        itemsPrice: total,
        taxPrice: total * 0.08,
        shippingPrice: 0,
        totalPrice: total * 1.08
      };

      const { data } = await axios.post('/api/orders', orderData, {
        headers: { Authorization: `Bearer ${userInfo.token}` }
      });

      // Clear cart
      localStorage.removeItem('cartItems');
      
      toast.success('Order placed successfully!');
      navigate(`/orders/${data._id}`);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="checkout-page">
      <div className="container">
        <div className="checkout-header">
          <h1>Checkout</h1>
          <div className="checkout-steps">
            <div className="step active">
              <span>1</span>
              <span>Shipping</span>
            </div>
            <div className="step active">
              <span>2</span>
              <span>Payment</span>
            </div>
            <div className="step">
              <span>3</span>
              <span>Review</span>
            </div>
          </div>
        </div>

        <div className="checkout-content">
          <form onSubmit={handleSubmit} className="checkout-form">
            {/* Shipping Address */}
            <div className="form-section">
              <div className="section-header">
                <FaTruck className="section-icon" />
                <h3>Shipping Address</h3>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="fullName">Full Name</label>
                  <input
                    type="text"
                    id="fullName"
                    name="fullName"
                    value={shippingAddress.fullName}
                    onChange={handleAddressChange}
                    required
                    className="form-control"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="address">Address</label>
                  <input
                    type="text"
                    id="address"
                    name="address"
                    value={shippingAddress.address}
                    onChange={handleAddressChange}
                    required
                    className="form-control"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="city">City</label>
                  <input
                    type="text"
                    id="city"
                    name="city"
                    value={shippingAddress.city}
                    onChange={handleAddressChange}
                    required
                    className="form-control"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="postalCode">Postal Code</label>
                  <input
                    type="text"
                    id="postalCode"
                    name="postalCode"
                    value={shippingAddress.postalCode}
                    onChange={handleAddressChange}
                    required
                    className="form-control"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="country">Country</label>
                  <select
                    id="country"
                    name="country"
                    value={shippingAddress.country}
                    onChange={handleAddressChange}
                    required
                    className="form-control"
                  >
                    <option value="">Select Country</option>
                    <option value="US">United States</option>
                    <option value="CA">Canada</option>
                    <option value="UK">United Kingdom</option>
                    <option value="AU">Australia</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Payment Method */}
            <div className="form-section">
              <div className="section-header">
                <FaCreditCard className="section-icon" />
                <h3>Payment Method</h3>
              </div>

              <div className="payment-options">
                <label className="payment-option">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="paypal"
                    checked={paymentMethod === 'paypal'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  <div className="payment-option-content">
                    <img src="/api/placeholder/40/25" alt="PayPal" />
                    <span>PayPal</span>
                  </div>
                </label>

                <label className="payment-option">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="stripe"
                    checked={paymentMethod === 'stripe'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  <div className="payment-option-content">
                    <img src="/api/placeholder/40/25" alt="Stripe" />
                    <span>Credit Card</span>
                  </div>
                </label>
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="btn btn-primary place-order-btn"
            >
              <FaLock />
              {loading ? 'Processing...' : `Place Order - $${(total * 1.08).toFixed(2)}`}
            </button>
          </form>

          {/* Order Summary */}
          <div className="order-summary">
            <h3>Order Summary</h3>

            <div className="order-items">
              {cartItems.map(item => (
                <div key={item._id} className="order-item">
                  <img src={item.images[0] || '/api/placeholder/60/60'} alt={item.name} />
                  <div className="item-info">
                    <h4>{item.name}</h4>
                    <p>Size: {item.selectedSize} | Color: {item.selectedColor}</p>
                    <p>Qty: {item.quantity}</p>
                  </div>
                  <div className="item-price">
                    ${(item.price * item.quantity).toFixed(2)}
                  </div>
                </div>
              ))}
            </div>

            <div className="order-totals">
              <div className="total-line">
                <span>Subtotal:</span>
                <span>${total.toFixed(2)}</span>
              </div>
              <div className="total-line">
                <span>Shipping:</span>
                <span>Free</span>
              </div>
              <div className="total-line">
                <span>Tax:</span>
                <span>${(total * 0.08).toFixed(2)}</span>
              </div>
              <div className="total-line total">
                <span>Total:</span>
                <span>${(total * 1.08).toFixed(2)}</span>
              </div>
            </div>

            <div className="security-notice">
              <FaLock />
              <span>Your payment information is secure and encrypted</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
