import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  FaUsers, 
  FaShoppingBag, 
  FaDollarSign, 
  FaBox,
  FaChartLine,  // ✅ Use this instead (trending/growth icon)
  FaEye,
  FaEdit,
  FaTrash
} from 'react-icons/fa';
import './Dashboard.css';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalOrders: 0,
    totalRevenue: 0,
    totalProducts: 0
  });
  const [recentOrders, setRecentOrders] = useState([]);
  const [topProducts, setTopProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const adminInfo = JSON.parse(localStorage.getItem('adminInfo') || '{}');
      if (!adminInfo.token) {
        window.location.href = '/admin/login';
        return;
      }

      // Simulate API calls for dashboard data
      setStats({
        totalUsers: 1250,
        totalOrders: 345,
        totalRevenue: 45670.50,
        totalProducts: 89
      });

      setRecentOrders([
        {
          id: '1',
          customer: 'Sarah Johnson',
          total: 127.50,
          status: 'processing',
          date: '2024-01-15'
        },
        {
          id: '2',
          customer: 'Emma Wilson',
          total: 89.99,
          status: 'shipped',
          date: '2024-01-14'
        },
        {
          id: '3',
          customer: 'Lisa Brown',
          total: 156.75,
          status: 'delivered',
          date: '2024-01-14'
        }
      ]);

      setTopProducts([
        {
          id: '1',
          name: 'High-Performance Sports Bra',
          sales: 145,
          revenue: 7250.00
        },
        {
          id: '2',
          name: 'Ultra Comfort Leggings',
          sales: 132,
          revenue: 9240.00
        },
        {
          id: '3',
          name: 'Breathable Tank Top',
          sales: 98,
          revenue: 3920.00
        }
      ]);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'processing':
        return 'status-processing';
      case 'shipped':
        return 'status-shipped';
      case 'delivered':
        return 'status-delivered';
      default:
        return 'status-pending';
    }
  };

  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="container">
          <div className="loading">Loading dashboard...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-page">
      <div className="container">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
          <p>Welcome back! Here's what's happening with your store.</p>
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon users">
              <FaUsers />
            </div>
            <div className="stat-details">
              <h3>{stats.totalUsers.toLocaleString()}</h3>
              <p>Total Users</p>
              <span className="stat-trend">
                <FaChartLine /> +12% from last month
              </span>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon orders">
              <FaShoppingBag />
            </div>
            <div className="stat-details">
              <h3>{stats.totalOrders.toLocaleString()}</h3>
              <p>Total Orders</p>
              <span className="stat-trend">
                <FaChartLine /> +8% from last month
              </span>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon revenue">
              <FaDollarSign />
            </div>
            <div className="stat-details">
              <h3>${stats.totalRevenue.toLocaleString()}</h3>
              <p>Total Revenue</p>
              <span className="stat-trend">
                <FaChartLine /> +15% from last month
              </span>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon products">
              <FaBox />
            </div>
            <div className="stat-details">
              <h3>{stats.totalProducts.toLocaleString()}</h3>
              <p>Total Products</p>
              <span className="stat-trend">
                <FaChartLine /> +5% from last month

              </span>
            </div>
          </div>
        </div>

        <div className="dashboard-content">
          {/* Recent Orders */}
          <div className="dashboard-section">
            <div className="section-header">
              <h3>Recent Orders</h3>
              <a href="/admin/orders" className="view-all-link">View All</a>
            </div>
            <div className="orders-table">
              <div className="table-header">
                <span>Order ID</span>
                <span>Customer</span>
                <span>Total</span>
                <span>Status</span>
                <span>Date</span>
                <span>Actions</span>
              </div>
              {recentOrders.map(order => (
                <div key={order.id} className="table-row">
                  <span>#{order.id.padStart(6, '0')}</span>
                  <span>{order.customer}</span>
                  <span>${order.total}</span>
                  <span className={`status ${getStatusClass(order.status)}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                  <span>{new Date(order.date).toLocaleDateString()}</span>
                  <div className="actions">
                    <button className="action-btn view">
                      <FaEye />
                    </button>
                    <button className="action-btn edit">
                      <FaEdit />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Top Products */}
          <div className="dashboard-section">
            <div className="section-header">
              <h3>Top Selling Products</h3>
              <a href="/admin/products" className="view-all-link">View All</a>
            </div>
            <div className="products-list">
              {topProducts.map(product => (
                <div key={product.id} className="product-item">
                  <div className="product-info">
                    <h4>{product.name}</h4>
                    <div className="product-stats">
                      <span>{product.sales} sales</span>
                      <span className="revenue">${product.revenue.toLocaleString()}</span>
                    </div>
                  </div>
                  <div className="product-actions">
                    <button className="action-btn edit">
                      <FaEdit />
                    </button>
                    <button className="action-btn delete">
                      <FaTrash />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="quick-actions">
          <h3>Quick Actions</h3>
          <div className="actions-grid">
            <a href="/admin/add-product" className="action-card">
              <FaBox />
              <span>Add New Product</span>
            </a>
            <a href="/admin/orders" className="action-card">
              <FaShoppingBag />
              <span>View All Orders</span>
            </a>
            <a href="/admin/users" className="action-card">
              <FaUsers />
              <span>Manage Users</span>
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
