import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaStar, FaHeart, FaShoppingCart } from 'react-icons/fa';
import './Home.css';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      const { data } = await axios.get('/api/products/featured');
      setFeaturedProducts(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching featured products:', error);
      setLoading(false);
    }
  };

  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem('cartItems') || '[]');
    const existingItem = existingCart.find(item => item._id === product._id);
    
    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }
    
    localStorage.setItem('cartItems', JSON.stringify(existingCart));
  };

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Empower Your Fitness Journey</h1>
          <p>Discover premium women's sportswear designed for performance, comfort, and style</p>
          <Link to="/products" className="btn btn-primary hero-btn">Shop Now</Link>
        </div>
        <div className="hero-image">
          <img src="/api/placeholder/600/400" alt="Fitness Woman" />
        </div>
      </section>

      {/* Features Section */}
      <section className="features">
        <div className="container">
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">🏃‍♀️</div>
              <h3>Performance Focused</h3>
              <p>Engineered for maximum performance during your workouts</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">💎</div>
              <h3>Premium Quality</h3>
              <p>High-quality materials that last through intense training</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">✨</div>
              <h3>Stylish Design</h3>
              <p>Look great while crushing your fitness goals</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">🚚</div>
              <h3>Fast Shipping</h3>
              <p>Quick delivery to get you moving sooner</p>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Products */}
      <section className="featured-products">
        <div className="container">
          <h2 className="section-title">Featured Products</h2>
          {loading ? (
            <div className="loading">Loading...</div>
          ) : (
            <div className="products-grid">
              {featuredProducts.map(product => (
                <div key={product._id} className="product-card">
                  <div className="product-image">
                    <img src={product.images[0] || '/api/placeholder/300/300'} alt={product.name} />
                    <div className="product-overlay">
                      <button className="btn-icon" onClick={() => addToCart(product)}>
                        <FaShoppingCart />
                      </button>
                      <button className="btn-icon">
                        <FaHeart />
                      </button>
                    </div>
                  </div>
                  <div className="product-info">
                    <h3>{product.name}</h3>
                    <div className="product-rating">
                      {[...Array(5)].map((_, i) => (
                        <FaStar key={i} className={i < product.rating ? 'star filled' : 'star'} />
                      ))}
                      <span>({product.numReviews})</span>
                    </div>
                    <div className="product-price">
                      <span className="current-price">${product.price}</span>
                      {product.originalPrice > product.price && (
                        <span className="original-price">${product.originalPrice}</span>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
          <div className="text-center mt-3">
            <Link to="/products" className="btn btn-outline">View All Products</Link>
          </div>
        </div>
      </section>

      {/* Newsletter */}
      <section className="newsletter">
        <div className="container">
          <div className="newsletter-content">
            <h2>Stay Updated</h2>
            <p>Get the latest updates on new arrivals and exclusive offers</p>
            <div className="newsletter-form">
              <input type="email" placeholder="Enter your email" className="form-control" />
              <button className="btn btn-primary">Subscribe</button>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
