import React, { useState } from 'react';
import './Signup.css';
import axios from 'axios';

const Signup = () => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    otp: '',
    password: '',
    confirmPassword: ''
  });
  const [otpSent, setOtpSent] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const sendOtp = async () => {
    if (!form.phone.match(/^\d{10}$/)) {
      alert('Enter valid 10-digit phone number');
      return;
    }
    try {
      // Backend sends OTP
      await axios.post('/api/send-otp', { phone: form.phone });
      setOtpSent(true);
      alert('OTP sent to your phone');
    } catch (err) {
      alert('Failed to send OTP');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, phone, otp, password, confirmPassword } = form;

    if (!name || !email.endsWith('.com') || !phone || !otp || !password || !confirmPassword) {
      alert('Fill all fields with valid input');
      return;
    }

    if (password !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    try {
      await axios.post('/api/signup', form);
      alert('Signup successful!');
    } catch (error) {
      alert('Signup failed');
    }
  };

  return (
    <div className="signup-container">
      <form className="signup-form" onSubmit={handleSubmit}>
        <h2>Create Account</h2>
        <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email (.com only)" onChange={handleChange} required />
        <input type="tel" name="phone" placeholder="Phone Number" onChange={handleChange} required />
        <button type="button" onClick={sendOtp} disabled={otpSent}>Send OTP</button>
        <input type="text" name="otp" placeholder="Enter OTP" onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
        <input type="password" name="confirmPassword" placeholder="Confirm Password" onChange={handleChange} required />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default Signup;
