import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { FaUpload, FaPlus, FaTimes } from 'react-icons/fa';
import './AddProduct.css';

const AddProduct = () => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    originalPrice: '',
    category: '',
    brand: '',
    sizes: [],
    colors: [],
    stock: '',
    featured: false,
    images: []
  });
  const [loading, setLoading] = useState(false);
  const [newColor, setNewColor] = useState('');

  const navigate = useNavigate();

  const categories = ['Tops', 'Bottoms', 'Sports Bras', 'Shoes', 'Accessories', 'Activewear Sets'];
  const brands = ['Nike', 'Adidas', 'Under Armour', 'Lululemon', 'Reebok', 'Puma'];
  const availableSizes = ['XS', 'S', 'M', 'L', 'XL', 'XXL'];
  const popularColors = ['Black', 'White', 'Gray', 'Navy', 'Pink', 'Red', 'Blue', 'Green'];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSizeChange = (size) => {
    setFormData(prev => ({
      ...prev,
      sizes: prev.sizes.includes(size)
        ? prev.sizes.filter(s => s !== size)
        : [...prev.sizes, size]
    }));
  };

  const handleColorChange = (color) => {
    setFormData(prev => ({
      ...prev,
      colors: prev.colors.includes(color)
        ? prev.colors.filter(c => c !== color)
        : [...prev.colors, color]
    }));
  };

  const addCustomColor = () => {
    if (newColor && !formData.colors.includes(newColor)) {
      setFormData(prev => ({
        ...prev,
        colors: [...prev.colors, newColor]
      }));
      setNewColor('');
    }
  };

  const removeColor = (color) => {
    setFormData(prev => ({
      ...prev,
      colors: prev.colors.filter(c => c !== color)
    }));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    // In a real app, you would upload these to a cloud service
    const imageUrls = files.map(file => URL.createObjectURL(file));
    setFormData(prev => ({
      ...prev,
      images: [...prev.images, ...imageUrls]
    }));
  };

  const removeImage = (index) => {
    setFormData(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (formData.sizes.length === 0) {
      toast.error('Please select at least one size');
      return;
    }

    if (formData.colors.length === 0) {
      toast.error('Please select at least one color');
      return;
    }

    if (formData.images.length === 0) {
      toast.error('Please upload at least one image');
      return;
    }

    setLoading(true);

    try {
      const adminInfo = JSON.parse(localStorage.getItem('adminInfo') || '{}');
      
      await axios.post('/api/products', formData, {
        headers: { Authorization: `Bearer ${adminInfo.token}` }
      });

      toast.success('Product added successfully!');
      navigate('/admin/dashboard');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error adding product');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-page">
      <div className="container">
        <div className="page-header">
          <h1>Add New Product</h1>
          <p>Create a new product listing for your store</p>
        </div>

        <form onSubmit={handleSubmit} className="add-product-form">
          <div className="form-sections">
            {/* Basic Information */}
            <div className="form-section">
              <h3>Basic Information</h3>
              
              <div className="form-group">
                <label htmlFor="name">Product Name *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  className="form-control"
                  placeholder="Enter product name"
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">Description *</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  rows="4"
                  className="form-control"
                  placeholder="Describe your product..."
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="category">Category *</label>
                  <select
                    id="category"
                    name="category"
                    value={formData.category}
                    onChange={handleChange}
                    required
                    className="form-control"
                  >
                    <option value="">Select Category</option>
                    {categories.map(cat => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="brand">Brand *</label>
                  <select
                    id="brand"
                    name="brand"
                    value={formData.brand}
                    onChange={handleChange}
                    required
                    className="form-control"
                  >
                    <option value="">Select Brand</option>
                    {brands.map(brand => (
                      <option key={brand} value={brand}>{brand}</option>
                    ))}
                  </select>
                </div>
              </div>
            </div>

            {/* Pricing & Inventory */}
            <div className="form-section">
              <h3>Pricing & Inventory</h3>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="price">Current Price *</label>
                  <input
                    type="number"
                    id="price"
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                    required
                    min="0"
                    step="0.01"
                    className="form-control"
                    placeholder="0.00"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="originalPrice">Original Price</label>
                  <input
                    type="number"
                    id="originalPrice"
                    name="originalPrice"
                    value={formData.originalPrice}
                    onChange={handleChange}
                    min="0"
                    step="0.01"
                    className="form-control"
                    placeholder="0.00"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="stock">Stock Quantity *</label>
                  <input
                    type="number"
                    id="stock"
                    name="stock"
                    value={formData.stock}
                    onChange={handleChange}
                    required
                    min="0"
                    className="form-control"
                    placeholder="0"
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="featured"
                    checked={formData.featured}
                    onChange={handleChange}
                  />
                  <span>Featured Product</span>
                </label>
              </div>
            </div>

            {/* Variants */}
            <div className="form-section">
              <h3>Product Variants</h3>
              
              <div className="form-group">
                <label>Available Sizes *</label>
                <div className="size-options">
                  {availableSizes.map(size => (
                    <label key={size} className="size-option">
                      <input
                        type="checkbox"
                        checked={formData.sizes.includes(size)}
                        onChange={() => handleSizeChange(size)}
                      />
                      <span>{size}</span>
                    </label>
                  ))}
                </div>
              </div>

              <div className="form-group">
                <label>Available Colors *</label>
                <div className="color-section">
                  <div className="color-options">
                    {popularColors.map(color => (
                      <label key={color} className="color-option">
                        <input
                          type="checkbox"
                          checked={formData.colors.includes(color)}
                          onChange={() => handleColorChange(color)}
                        />
                        <span className="color-dot" style={{ backgroundColor: color.toLowerCase() }}></span>
                        <span>{color}</span>
                      </label>
                    ))}
                  </div>
                  
                  <div className="custom-color">
                    <input
                      type="text"
                      value={newColor}
                      onChange={(e) => setNewColor(e.target.value)}
                      placeholder="Custom color"
                      className="form-control"
                    />
                    <button type="button" onClick={addCustomColor} className="btn btn-outline">
                      <FaPlus /> Add
                    </button>
                  </div>

                  {formData.colors.length > 0 && (
                    <div className="selected-colors">
                      <span>Selected colors:</span>
                      <div className="color-tags">
                        {formData.colors.map(color => (
                          <div key={color} className="color-tag">
                            <span className="color-dot" style={{ backgroundColor: color.toLowerCase() }}></span>
                            <span>{color}</span>
                            <button type="button" onClick={() => removeColor(color)}>
                              <FaTimes />
                            </button>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Images */}
            <div className="form-section">
              <h3>Product Images</h3>
              
              <div className="form-group">
                <label htmlFor="images">Upload Images *</label>
                <div className="image-upload">
                  <input
                    type="file"
                    id="images"
                    multiple
                    accept="image/*"
                    onChange={handleImageUpload}
                    className="file-input"
                  />
                  <label htmlFor="images" className="upload-label">
                    <FaUpload />
                    <span>Click to upload images</span>
                    <small>PNG, JPG, JPEG up to 10MB each</small>
                  </label>
                </div>

                {formData.images.length > 0 && (
                  <div className="image-preview">
                    {formData.images.map((image, index) => (
                      <div key={index} className="preview-item">
                        <img src={image} alt={`Preview ${index + 1}`} />
                        <button
                          type="button"
                          className="remove-image"
                          onClick={() => removeImage(index)}
                        >
                          <FaTimes />
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Submit Button */}
          <div className="form-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => navigate('/admin/dashboard')}
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="btn btn-primary"
            >
              {loading ? 'Adding Product...' : 'Add Product'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddProduct;
