import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaUser, FaEdit, FaTrash, FaSearch, FaUserShield, FaBan } from 'react-icons/fa';
import { toast } from 'react-toastify';
import './Users.css';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('newest');

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    filterAndSortUsers();
  }, [users, searchTerm, sortBy]);

  const fetchUsers = async () => {
    try {
      const adminInfo = JSON.parse(localStorage.getItem('adminInfo') || '{}');
      if (!adminInfo.token) {
        window.location.href = '/admin/login';
        return;
      }

      // Simulate API call with mock data
      const mockUsers = [
        {
          _id: '64user123',
          name: 'Sarah Johnson',
          email: 'sarah@email.com',
          isAdmin: false,
          phone: '+1-555-0123',
          createdAt: '2024-01-10T10:30:00Z',
          lastLogin: '2024-01-16T08:45:00Z',
          orderCount: 5,
          totalSpent: 450.75,
          status: 'active'
        },
        {
          _id: '64user456',
          name: 'Emma Wilson',
          email: 'emma@email.com',
          isAdmin: false,
          phone: '+1-555-0456',
          createdAt: '2024-01-08T14:20:00Z',
          lastLogin: '2024-01-15T16:30:00Z',
          orderCount: 3,
          totalSpent: 275.50,
          status: 'active'
        },
        {
          _id: '64user789',
          name: 'Lisa Brown',
          email: 'lisa@email.com',
          isAdmin: true,
          phone: '+1-555-0789',
          createdAt: '2023-12-15T09:15:00Z',
          lastLogin: '2024-01-16T12:00:00Z',
          orderCount: 8,
          totalSpent: 680.25,
          status: 'active'
        },
        {
          _id: '64user012',
          name: 'Maria Garcia',
          email: 'maria@email.com',
          isAdmin: false,
          phone: '+1-555-0012',
          createdAt: '2024-01-12T11:45:00Z',
          lastLogin: '2024-01-14T19:20:00Z',
          orderCount: 2,
          totalSpent: 125.99,
          status: 'active'
        },
        {
          _id: '64user345',
          name: 'Jennifer Davis',
          email: 'jennifer@email.com',
          isAdmin: false,
          phone: '+1-555-0345',
          createdAt: '2023-11-20T16:30:00Z',
          lastLogin: '2024-01-10T10:15:00Z',
          orderCount: 12,
          totalSpent: 890.40,
          status: 'blocked'
        }
      ];

      setUsers(mockUsers);
      setLoading(false);
    } catch (error) {
      toast.error('Error loading users');
      setLoading(false);
    }
  };

  const filterAndSortUsers = () => {
    let filtered = users.filter(user =>
      user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Sort users
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'newest':
          return new Date(b.createdAt) - new Date(a.createdAt);
        case 'oldest':
          return new Date(a.createdAt) - new Date(b.createdAt);
        case 'name':
          return a.name.localeCompare(b.name);
        case 'orders':
          return b.orderCount - a.orderCount;
        case 'spent':
          return b.totalSpent - a.totalSpent;
        default:
          return 0;
      }
    });

    setFilteredUsers(filtered);
  };

  const toggleUserStatus = async (userId, currentStatus) => {
    try {
      const newStatus = currentStatus === 'active' ? 'blocked' : 'active';
      
      setUsers(prev => prev.map(user =>
        user._id === userId ? { ...user, status: newStatus } : user
      ));

      toast.success(`User ${newStatus === 'active' ? 'activated' : 'blocked'} successfully`);
    } catch (error) {
      toast.error('Error updating user status');
    }
  };

  const toggleAdminStatus = async (userId, isCurrentlyAdmin) => {
    try {
      setUsers(prev => prev.map(user =>
        user._id === userId ? { ...user, isAdmin: !isCurrentlyAdmin } : user
      ));

      toast.success(`Admin privileges ${!isCurrentlyAdmin ? 'granted' : 'revoked'} successfully`);
    } catch (error) {
      toast.error('Error updating admin status');
    }
  };

  const deleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
      try {
        setUsers(prev => prev.filter(user => user._id !== userId));
        toast.success('User deleted successfully');
      } catch (error) {
        toast.error('Error deleting user');
      }
    }
  };

  if (loading) {
    return (
      <div className="users-page">
        <div className="container">
          <div className="loading">Loading users...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="users-page">
      <div className="container">
        <div className="page-header">
          <h1>User Management</h1>
          <p>Manage all registered users and their permissions</p>
        </div>

        {/* Controls */}
        <div className="users-controls">
          <div className="search-bar">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search users by name or email..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="sort-select"
          >
            <option value="newest">Newest First</option>
            <option value="oldest">Oldest First</option>
            <option value="name">Name (A-Z)</option>
            <option value="orders">Most Orders</option>
            <option value="spent">Highest Spending</option>
          </select>
        </div>

        {/* Users Grid */}
        <div className="users-grid">
          {filteredUsers.length > 0 ? (
            filteredUsers.map(user => (
              <div key={user._id} className="user-card">
                <div className="user-header">
                  <div className="user-avatar">
                    <FaUser />
                  </div>
                  <div className="user-badges">
                    {user.isAdmin && (
                      <span className="badge admin">Admin</span>
                    )}
                    <span className={`badge status ${user.status}`}>
                      {user.status.charAt(0).toUpperCase() + user.status.slice(1)}
                    </span>
                  </div>
                </div>

                <div className="user-info">
                  <h3>{user.name}</h3>
                  <p className="user-email">{user.email}</p>
                  <p className="user-phone">{user.phone}</p>
                </div>

                <div className="user-stats">
                  <div className="stat">
                    <span className="stat-value">{user.orderCount}</span>
                    <span className="stat-label">Orders</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">${user.totalSpent.toFixed(0)}</span>
                    <span className="stat-label">Spent</span>
                  </div>
                </div>

                <div className="user-dates">
                  <div className="date-info">
                    <span className="date-label">Joined:</span>
                    <span>{new Date(user.createdAt).toLocaleDateString()}</span>
                  </div>
                  <div className="date-info">
                    <span className="date-label">Last Login:</span>
                    <span>{new Date(user.lastLogin).toLocaleDateString()}</span>
                  </div>
                </div>

                <div className="user-actions">
                  <button
                    className={`action-btn ${user.isAdmin ? 'remove-admin' : 'make-admin'}`}
                    onClick={() => toggleAdminStatus(user._id, user.isAdmin)}
                    title={user.isAdmin ? 'Remove Admin' : 'Make Admin'}
                  >
                    <FaUserShield />
                  </button>

                  <button
                    className={`action-btn ${user.status === 'active' ? 'block' : 'activate'}`}
                    onClick={() => toggleUserStatus(user._id, user.status)}
                    title={user.status === 'active' ? 'Block User' : 'Activate User'}
                  >
                    <FaBan />
                  </button>

                  <button
                    className="action-btn edit"
                    title="Edit User"
                  >
                    <FaEdit />
                  </button>

                  <button
                    className="action-btn delete"
                    onClick={() => deleteUser(user._id)}
                    title="Delete User"
                  >
                    <FaTrash />
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div className="no-users">
              <h3>No users found</h3>
              <p>No users match your search criteria</p>
            </div>
          )}
        </div>

        {/* Summary Stats */}
        <div className="users-summary">
          <div className="summary-card">
            <h4>Total Users</h4>
            <span>{filteredUsers.length}</span>
          </div>
          <div className="summary-card">
            <h4>Active Users</h4>
            <span>
              {filteredUsers.filter(user => user.status === 'active').length}
            </span>
          </div>
          <div className="summary-card">
            <h4>Admin Users</h4>
            <span>
              {filteredUsers.filter(user => user.isAdmin).length}
            </span>
          </div>
          <div className="summary-card">
            <h4>Total Revenue</h4>
            <span>
              ${filteredUsers.reduce((sum, user) => sum + user.totalSpent, 0).toFixed(2)}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Users;
