import React, { useEffect, useState } from 'react';
import './Profile.css';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', phone: '' });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setUser(res.data);
        setFormData({
          name: res.data.name,
          email: res.data.email,
          phone: res.data.phone || ''
        });
      } catch (err) {
        console.error(err);
        alert("❌ Failed to fetch profile data.");
      }
    };
    fetchUser();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put('http://localhost:5000/api/users/profile', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      alert('✅ Profile updated successfully.');
      setUser(res.data);
      setEditMode(false);
    } catch (err) {
      console.error(err);
      alert('❌ Update failed.');
    }
  };

  if (!user) return <div className="profile-container">Loading...</div>;

  return (
    <div className="profile-container">
      <h2>👤 My Profile</h2>
      {!editMode ? (
        <div className="profile-card">
          <p><strong>👨 Name:</strong> {user.name}</p>
          <p><strong>📧 Email:</strong> {user.email}</p>
          <p><strong>📱 Phone:</strong> {user.phone || 'N/A'}</p>
          <button className="edit-btn" onClick={() => setEditMode(true)}>✏️ Edit Profile</button>
        </div>
      ) : (
        <form className="profile-form" onSubmit={handleSubmit}>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required placeholder="Your Name" />
          <input type="email" name="email" value={formData.email} onChange={handleChange} required placeholder="Email" />
          <input type="text" name="phone" value={formData.phone} onChange={handleChange} placeholder="Phone (optional)" />
          <div className="form-buttons">
            <button type="submit" className="update-btn">💾 Update</button>
            <button type="button" className="cancel-btn" onClick={() => setEditMode(false)}>❌ Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default Profile;
