import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isUserDropdownOpen, setIsUserDropdownOpen] = useState(false);
  const [cartItemCount, setCartItemCount] = useState(3); // This would come from your cart state
  const [isLoggedIn, setIsLoggedIn] = useState(false); // This would come from your auth state
  
  const navigate = useNavigate();

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleUserDropdown = () => {
    setIsUserDropdownOpen(!isUserDropdownOpen);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/products?search=${searchQuery}`);
      setSearchQuery('');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setIsUserDropdownOpen(false);
    // Add your logout logic here
    navigate('/');
  };

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        {/* Logo */}
        <Link to="/" className="navbar-logo">
          <div className="logo-container">
            <span className="logo-icon">⚡</span>
            <span className="logo-text">SPORTEX</span>
          </div>
        </Link>

        {/* Desktop Navigation Links */}
        <ul className={`navbar-menu ${isMenuOpen ? 'active' : ''}`}>
          <li className="navbar-item">
            <Link to="/" className="navbar-link" onClick={() => setIsMenuOpen(false)}>
              Home
            </Link>
          </li>
          <li className="navbar-item dropdown">
            <Link to="/products" className="navbar-link" onClick={() => setIsMenuOpen(false)}>
              Products
              <span className="dropdown-icon">▼</span>
            </Link>
            <div className="dropdown-menu">
              <Link to="/products?category=running" className="dropdown-link">Running</Link>
              <Link to="/products?category=gym" className="dropdown-link">Gym & Fitness</Link>
              <Link to="/products?category=outdoor" className="dropdown-link">Outdoor</Link>
              <Link to="/products?category=athleisure" className="dropdown-link">Athleisure</Link>
            </div>
          </li>
          <li className="navbar-item">
            <Link to="/about" className="navbar-link" onClick={() => setIsMenuOpen(false)}>
              About
            </Link>
          </li>
          <li className="navbar-item">
            <Link to="/contact" className="navbar-link" onClick={() => setIsMenuOpen(false)}>
              Contact
            </Link>
          </li>
          <li className="navbar-item">
            <Link to="/faq" className="navbar-link" onClick={() => setIsMenuOpen(false)}>
              FAQ
            </Link>
          </li>
        </ul>

        {/* Search Bar */}
        <div className="navbar-search">
          <form onSubmit={handleSearch} className="search-form">
            <input
              type="text"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button type="submit" className="search-btn">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                <path d="M21 21L16.514 16.506L21 21ZM19 10.5C19 15.194 15.194 19 10.5 19C5.806 19 2 15.194 2 10.5C2 5.806 5.806 2 10.5 2C15.194 2 19 5.806 19 10.5Z" stroke="currentColor" strokeWidth="2"/>
              </svg>
            </button>
          </form>
        </div>

        {/* Right Side Icons */}
        <div className="navbar-actions">
          {/* User Account */}
          <div className="user-menu">
            {isLoggedIn ? (
              <div className="user-dropdown">
                <button className="user-btn" onClick={toggleUserDropdown}>
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                    <path d="M20 21V19C20 17.9391 19.5786 16.9217 18.8284 16.1716C18.0783 15.4214 17.0609 15 16 15H8C6.93913 15 5.92172 15.4214 5.17157 16.1716C4.42143 16.9217 4 17.9391 4 19V21" stroke="currentColor" strokeWidth="2"/>
                    <circle cx="12" cy="7" r="4" stroke="currentColor" strokeWidth="2"/>
                  </svg>
                </button>
                {isUserDropdownOpen && (
                  <div className="user-dropdown-menu">
                    <Link to="/profile" className="dropdown-link" onClick={() => setIsUserDropdownOpen(false)}>
                      My Profile
                    </Link>
                    <Link to="/orders" className="dropdown-link" onClick={() => setIsUserDropdownOpen(false)}>
                      My Orders
                    </Link>
                    <Link to="/wishlist" className="dropdown-link" onClick={() => setIsUserDropdownOpen(false)}>
                      Wishlist
                    </Link>
                    <button className="dropdown-link logout-btn" onClick={handleLogout}>
                      Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <Link to="/login" className="login-btn">
                Login
              </Link>
            )}
          </div>

          {/* Shopping Cart */}
          <Link to="/cart" className="cart-btn">
            <div className="cart-icon-container">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path d="M3 3H5L5.4 5M7 13H17L21 5H5.4M7 13L5.4 5M7 13L4.7 15.3C4.3 15.7 4.6 16.5 5.1 16.5H17M17 13V17C17 18.1 16.1 19 15 19H9C7.9 19 7 18.1 7 17V13H17Z" stroke="currentColor" strokeWidth="2"/>
              </svg>
              {cartItemCount > 0 && (
                <span className="cart-badge">{cartItemCount}</span>
              )}
            </div>
          </Link>

          {/* Mobile Menu Toggle */}
          <button 
            className={`mobile-menu-btn ${isMenuOpen ? 'active' : ''}`}
            onClick={toggleMenu}
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>

      {/* Mobile Menu Overlay */}
      {isMenuOpen && <div className="mobile-overlay" onClick={() => setIsMenuOpen(false)}></div>}
    </nav>
  );
};

export default Navbar;
