import React, { useState } from 'react';
import './Signup.css';
import axios from 'axios';

const Signup = () => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    otp: '',
    password: '',
    confirmPassword: '',
  });
  const [otpSent, setOtpSent] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const sendOtp = async () => {
    if (!form.email.endsWith('.com')) {
      setError('Email must end with .com');
      return;
    }
    try {
      await axios.post('/api/send-email-otp', { email: form.email });
      setOtpSent(true);
      setError('');
      setSuccess('OTP sent to email');
    } catch {
      setError('Failed to send OTP');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name || !form.email || !form.otp || !form.password || !form.confirmPassword) {
      setError('All fields are required');
      return;
    }
    if (form.password !== form.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      await axios.post('/api/signup', form);
      setSuccess('Signup successful');
      setError('');
    } catch {
      setError('Signup failed');
    }
  };

  return (
    <div className="signup-container">
      <form className="signup-form" onSubmit={handleSubmit}>
        <h2>Signup</h2>
        {error && <p className="error">{error}</p>}
        {success && <p className="success">{success}</p>}
        <input type="text" name="name" placeholder="Name" onChange={handleChange} />
        <input type="email" name="email" placeholder="Email (.com only)" onChange={handleChange} />
        {!otpSent ? (
          <button type="button" onClick={sendOtp}>Send OTP to Email</button>
        ) : (
          <input type="text" name="otp" placeholder="Enter OTP" onChange={handleChange} />
        )}
        <input type="password" name="password" placeholder="Password" onChange={handleChange} />
        <input type="password" name="confirmPassword" placeholder="Confirm Password" onChange={handleChange} />
        <button type="submit">Signup</button>
      </form>
    </div>
  );
};

export default Signup;
