import React, { useState } from 'react';
import './Login.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [form, setForm] = useState({ email: '', password: '', phone: '', otp: '' });
  const [attempts, setAttempts] = useState(0);
  const [otpLogin, setOtpLogin] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/users/login', {
        email: form.email,
        password: form.password,
      });

      alert('Login successful');
      const { user, token } = res.data;

      // Save to localStorage (optional)
      localStorage.setItem('user', JSON.stringify(user));
      localStorage.setItem('token', token);

      // Role-based navigation
      if (user.role === 'admin') {
        navigate('/admin/dashboard');
      } else {
        navigate('/user/home');
      }
    } catch {
      setAttempts(prev => prev + 1);
      if (attempts + 1 >= 3) setOtpLogin(true);
      else alert('Incorrect email or password');
    }
  };

  const sendOtp = async () => {
    try {
      await axios.post('/api/users/send-otp', { phone: form.phone });
      setOtpSent(true);
      alert('OTP sent');
    } catch {
      alert('Failed to send OTP');
    }
  };

  const verifyOtp = async () => {
    try {
      const res = await axios.post('/api/users/login-otp', {
        phone: form.phone,
        otp: form.otp,
      });

      alert('Login via OTP successful');
      const { user, token } = res.data;

      // Save to localStorage
      localStorage.setItem('user', JSON.stringify(user));
      localStorage.setItem('token', token);

      // Role-based navigation
      if (user.role === 'admin') {
        navigate('/admin/dashboard');
      } else {
        navigate('/user/home');
      }
    } catch {
      alert('OTP login failed');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        {!otpLogin ? (
          <>
            <input
              type="email"
              name="email"
              placeholder="Email"
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              onChange={handleChange}
              required
            />
            <button type="submit">Login</button>
          </>
        ) : (
          <>
            <input
              type="tel"
              name="phone"
              placeholder="Phone Number"
              onChange={handleChange}
              required
            />
            <button type="button" onClick={sendOtp} disabled={otpSent}>
              Send OTP
            </button>
            <input
              type="text"
              name="otp"
              placeholder="Enter OTP"
              onChange={handleChange}
              required
            />
            <button type="button" onClick={verifyOtp}>
              Login via OTP
            </button>
          </>
        )}
      </form>
    </div>
  );
};

export default Login;
