import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { FaStar, FaHeart, FaShoppingCart, FaFilter } from 'react-icons/fa';
import { toast } from 'react-toastify';
import './Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    category: '',
    minPrice: '',
    maxPrice: '',
    brand: '',
    sort: 'newest'
  });
  const [showFilters, setShowFilters] = useState(false);
  const [searchParams] = useSearchParams();

  const categories = ['Tops', 'Bottoms', 'Sports Bras', 'Shoes', 'Accessories', 'Activewear Sets'];
  const brands = ['Nike', 'Adidas', 'Under Armour', 'Lululemon', 'Reebok', 'Puma'];

  useEffect(() => {
    const category = searchParams.get('category');
    if (category) {
      setFilters(prev => ({ ...prev, category }));
    }
    fetchProducts();
  }, [searchParams]);

  useEffect(() => {
    fetchProducts();
  }, [filters]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();
      
      Object.entries(filters).forEach(([key, value]) => {
        if (value) queryParams.append(key, value);
      });

      const { data } = await axios.get(`/api/products?${queryParams}`);
      setProducts(data.products);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching products:', error);
      setLoading(false);
      toast.error('Error loading products');
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const clearFilters = () => {
    setFilters({
      category: '',
      minPrice: '',
      maxPrice: '',
      brand: '',
      sort: 'newest'
    });
  };

  const addToCart = (product) => {
    const existingCart = JSON.parse(localStorage.getItem('cartItems') || '[]');
    const existingItem = existingCart.find(item => item._id === product._id);
    
    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1, selectedSize: 'M', selectedColor: product.colors[0] });
    }
    
    localStorage.setItem('cartItems', JSON.stringify(existingCart));
    toast.success('Product added to cart!');
  };

  const addToWishlist = async (productId) => {
    try {
      const userInfo = JSON.parse(localStorage.getItem('userInfo') || '{}');
      if (!userInfo.token) {
        toast.error('Please login to add to wishlist');
        return;
      }

      await axios.post('/api/users/wishlist', 
        { productId },
        { headers: { Authorization: `Bearer ${userInfo.token}` }}
      );
      toast.success('Added to wishlist!');
    } catch (error) {
      toast.error('Error adding to wishlist');
    }
  };

  return (
    <div className="products-page">
      <div className="container">
        <div className="page-header">
          <h1>Women's Sportswear</h1>
          <p>Discover our complete collection of premium activewear</p>
        </div>

        <div className="products-container">
          {/* Filters Sidebar */}
          <aside className={`filters-sidebar ${showFilters ? 'show' : ''}`}>
            <div className="filters-header">
              <h3>Filters</h3>
              <button className="clear-filters" onClick={clearFilters}>
                Clear All
              </button>
            </div>

            <div className="filter-group">
              <h4>Sort By</h4>
              <select 
                value={filters.sort} 
                onChange={(e) => handleFilterChange('sort', e.target.value)}
                className="form-control"
              >
                <option value="newest">Newest</option>
                <option value="price_asc">Price: Low to High</option>
                <option value="price_desc">Price: High to Low</option>
                <option value="rating">Best Rated</option>
              </select>
            </div>

            <div className="filter-group">
              <h4>Category</h4>
              <div className="filter-options">
                {categories.map(category => (
                  <label key={category} className="filter-option">
                    <input
                      type="radio"
                      name="category"
                      value={category}
                      checked={filters.category === category}
                      onChange={(e) => handleFilterChange('category', e.target.value)}
                    />
                    {category}
                  </label>
                ))}
              </div>
            </div>

            <div className="filter-group">
              <h4>Brand</h4>
              <div className="filter-options">
                {brands.map(brand => (
                  <label key={brand} className="filter-option">
                    <input
                      type="radio"
                      name="brand"
                      value={brand}
                      checked={filters.brand === brand}
                      onChange={(e) => handleFilterChange('brand', e.target.value)}
                    />
                    {brand}
                  </label>
                ))}
              </div>
            </div>

            <div className="filter-group">
              <h4>Price Range</h4>
              <div className="price-inputs">
                <input
                  type="number"
                  placeholder="Min"
                  value={filters.minPrice}
                  onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                  className="form-control"
                />
                <input
                  type="number"
                  placeholder="Max"
                  value={filters.maxPrice}
                  onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                  className="form-control"
                />
              </div>
            </div>
          </aside>

          {/* Products Grid */}
          <main className="products-main">
            <div className="products-header">
              <div className="results-info">
                <span>{products.length} products found</span>
              </div>
              <button 
                className="filter-toggle"
                onClick={() => setShowFilters(!showFilters)}
              >
                <FaFilter /> Filters
              </button>
            </div>

            {loading ? (
              <div className="loading">Loading products...</div>
            ) : (
              <div className="products-grid">
                {products.map(product => (
                  <div key={product._id} className="product-card">
                    <div className="product-image">
                      <img 
                        src={product.images[0] || '/api/placeholder/300/300'} 
                        alt={product.name} 
                      />
                      <div className="product-overlay">
                        <button 
                          className="btn-icon"
                          onClick={() => addToCart(product)}
                        >
                          <FaShoppingCart />
                        </button>
                        <button 
                          className="btn-icon"
                          onClick={() => addToWishlist(product._id)}
                        >
                          <FaHeart />
                        </button>
                      </div>
                    </div>
                    <div className="product-info">
                      <div className="brand">{product.brand}</div>
                      <h3>{product.name}</h3>
                      <div className="product-rating">
                        {[...Array(5)].map((_, i) => (
                          <FaStar 
                            key={i} 
                            className={i < product.rating ? 'star filled' : 'star'} 
                          />
                        ))}
                        <span>({product.numReviews})</span>
                      </div>
                      <div className="product-price">
                        <span className="current-price">${product.price}</span>
                        {product.originalPrice > product.price && (
                          <span className="original-price">${product.originalPrice}</span>
                        )}
                      </div>
                      <div className="product-colors">
                        {product.colors.slice(0, 4).map((color, index) => (
                          <span 
                            key={index} 
                            className="color-dot" 
                            style={{ backgroundColor: color.toLowerCase() }}
                          ></span>
                        ))}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {products.length === 0 && !loading && (
              <div className="no-products">
                <h3>No products found</h3>
                <p>Try adjusting your filters or search criteria</p>
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default Products;
